# and store it as a variable then use that variable as a delimiter
def f_get_delimiter (source_path):
    try:
        print("Source Path:", source_path)  # Debug: Print the source path
        headerlist = spark.sparkContext.textFile(source_path).take(1)
        header_str = ''.join(headerlist)

        results= re.search("(,|;|\\|)",header_str)
        return results.group()
    except Exception as err:
        print("Error Occured ", str(err))

